mapper.update:
UPDATE commitment_book SET deleted = 1, etime = now()
WHERE deleted = 0 AND uid IN 

	#{id}

UPDATE commitment_book t,
(SELECT credit_entity_name, table_owner_id from commitment_book where uid in

	#{id}

) a SET is_publish = #{ispublish}, etime = now()
WHERE t.deleted = 0 AND t.credit_entity_name = a.credit_entity_name AND t.table_owner_id = a.table_owner_id;

mapper.select:
	   	SELECT 

       	FROM commitment_book t
       	WHERE t.uid = #{value}

SELECT tt.uid, tt.credit_entity_name, tt.table_owner_id, tt.table_owner_name, tt.legal_person, tt.ctime, tt.comm_time, tt.etime, tt.is_publish, tt.creater, tt.book_name, tt.content, tt.img_file, tt.domain_type, tt.domain_name FROM ( SELECT t.uid, t.credit_entity_name, t.table_owner_id, t.table_owner_name, t.legal_person, t.ctime, t.comm_time, t.etime, t.is_publish, t.creater, t.book_name, t.content, GROUP_CONCAT(t.img_file) AS img_file, t.domain_type, g.region_id, g.region_name, t.order_value, t.domain_name FROM wzcredit.commitment_book t, rbac.sys_group g WHERE t.table_owner_id = g.group_path AND t.deleted = 0 AND t.img_file IS NOT NULL GROUP BY t.credit_entity_name, t.table_owner_id UNION SELECT t.uid, t.credit_entity_name, t.table_owner_id, t.table_owner_name, t.legal_person, t.ctime, t.comm_time, t.etime, t.is_publish, t.creater, t.book_name, t.content, t.img_file, t.domain_type, g.region_id, g.region_name, t.order_value, t.domain_name FROM wzcredit.commitment_book t, rbac.sys_group g WHERE t.table_owner_id = g.group_path AND t.deleted = 0 AND t.img_file IS NULL ) tt
AND tt.region_id = (SELECT region_id FROM rbac.sys_group WHERE group_id = #{siteId} )
AND tt.uid = #{uid}
AND tt.credit_entity_name = #{creditEntityName}
AND tt.legal_person = #{legalPerson}
AND tt.table_owner_id = #{tableOwnerId}
AND tt.table_owner_name = #{tableOwnerName}
AND tt.img_file = #{imgFile}
AND tt.ctime = #{ctime}
AND tt.etime = #{etime}
AND tt.is_publish = #{isPublish}
AND (tt.credit_entity_name LIKE binary concat('%',#{keyword},'%') OR tt.table_owner_name LIKE binary concat('%',#{keyword},'%') OR tt.creater LIKE binary concat('%',#{keyword},'%') OR tt.domain_name LIKE binary concat('%',#{keyword},'%'))
AND tt.credit_entity_name LIKE binary concat('%',#{keywordForFront},'%')
AND tt.ctime > #{searchNewDataTime}
AND tt.book_name = #{bookName}
AND tt.domain_type = #{domainType}
AND tt.order_value = #{orderValue}
AND tt.region_id = #{regionId}
AND tt.region_name = #{regionName}
ORDER BY tt.ctime DESC
ORDER BY tt.ctime DESC

SELECT COUNT(*) FROM (SELECT t.uid, t.credit_entity_name, t.table_owner_id, t.table_owner_name, t.legal_person, t.ctime, t.comm_time, t.etime, t.is_publish, t.creater, t.book_name, t.content, GROUP_CONCAT(t.img_file) AS img_file, t.domain_type, g.region_id, g.region_name, t.order_value, t.domain_name FROM wzcredit.commitment_book t, rbac.sys_group g WHERE t.table_owner_id = g.group_path AND t.deleted = 0 AND t.img_file IS NOT NULL GROUP BY t.credit_entity_name, t.table_owner_id UNION SELECT t.uid, t.credit_entity_name, t.table_owner_id, t.table_owner_name, t.legal_person, t.ctime, t.comm_time, t.etime, t.is_publish, t.creater, t.book_name, t.content, t.img_file, t.domain_type, g.region_id, g.region_name, t.order_value, t.domain_name FROM wzcredit.commitment_book t, rbac.sys_group g WHERE t.table_owner_id = g.group_path AND t.deleted = 0 AND t.img_file IS NULL ) tt
AND tt.region_id = (SELECT region_id FROM rbac.sys_group WHERE group_id = #{siteId} )
AND (tt.credit_entity_name LIKE binary concat('%',#{keyword},'%') OR tt.table_owner_name LIKE binary concat('%',#{keyword},'%') OR tt.creater LIKE binary concat('%',#{keyword},'%') OR tt.domain_name LIKE binary concat('%',#{keyword},'%'))
AND tt.credit_entity_name LIKE binary concat('%',#{keywordForFront},'%')
AND tt.uid = #{uid}
AND tt.credit_entity_name = #{creditEntityName}
AND tt.legal_person = #{legalPerson}
AND tt.table_owner_id = #{tableOwnerId}
AND tt.table_owner_name = #{tableOwnerName}
AND tt.img_file = #{imgFile}
AND tt.is_publish = #{isPublish}
AND tt.ctime = #{ctime}
AND tt.etime = #{etime}
AND tt.ctime > #{searchNewDataTime}
AND tt.book_name = #{bookName}
AND tt.domain_type = #{domainType}
AND tt.order_value = #{orderValue}
AND tt.region_id = #{regionId}
AND tt.region_name = #{regionName}

SELECT
	t.uid,
	t.table_owner_id,
	t.table_owner_name,
	g.group_alias AS groupAlias
FROM
	(
	SELECT 
		uid,
		table_owner_id,
		table_owner_name
    FROM commitment_book t


            AND uid = #{uid}


            AND credit_entity_name = #{creditEntityName}


            AND credit_entity_code = #{creditEntityCode}


            AND unify_society_code = #{unifySocietyCode}


            AND business_license = #{businessLicense}


            AND tax_register_number = #{taxRegisterNumber}


            AND legal_person = #{legalPerson}


            AND table_owner_id = #{tableOwnerId}


            AND table_owner_name = #{tableOwnerName}


            AND file_path = #{filePath}


            AND img_file = #{imgFile}


            AND is_publish = #{isPublish}


            AND ctime = #{ctime}


            AND creater_id = #{createrId}


            AND creater = #{creater}


            AND etime = #{etime}


            AND editor_id = #{editorId}


            AND editor = #{editor}


            AND remarks = #{remarks}


            AND deleted = #{deleted}


       		AND book_name = #{bookName},


       		AND domain_type = #{domainType},


       		AND order_value = #{orderValue},


	GROUP BY t.table_owner_id
	) t,
		rbac.sys_group g
	WHERE
		t.table_owner_id = g.group_path
	AND g.deleted = 0
	AND g.group_alias IS NOT NULL

	AND g.region_id = (SELECT region_id FROM rbac.sys_group WHERE group_id = #{siteId} )

	ORDER BY
		g.order_value ASC

